using System.ComponentModel.Composition;
using XrmToolBox.Extensibility;
using XrmToolBox.Extensibility.Interfaces;

namespace MessageExplorer
{
    // Do not forget to update version number and author (company attribute) in AssemblyInfo.cs class
    // To generate Base64 string for Images below, you can use https://www.base64-image.de/
    [Export(typeof(IXrmToolBoxPlugin)),
        ExportMetadata("Name", "Message Explorer"),
        ExportMetadata("Author", "Marius Agur \"CRMVIKING\" Lind"),
        ExportMetadata("Description", "This plugin allows you to show all plugins and workflows registered for a specified entity message/trigger"),
        // Please specify the base64 content of a 32x32 pixels image
        ExportMetadata("SmallImageBase64", ""),
        // Please specify the base64 content of a 80x80 pixels image
        ExportMetadata("BigImageBase64", ""),
        ExportMetadata("BackgroundColor", "Lavender"),
        ExportMetadata("PrimaryFontColor", "Black"),
        ExportMetadata("SecondaryFontColor", "Gray")]
    public class MessageExplorer : PluginBase
    {
        public override IXrmToolBoxPluginControl GetControl()
        {
            return new EntityExplorer();
        }
    }
}